","keccak256":"0xb1970fac7b64e6c09611e6691791e848d5e3fe410fa5899e7df2e0afd77a99e3","license":"MIT"},"contracts/Rubyscore_Deposit.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.21;

import {EIP712} from "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IRubyscore_Deposit} from "./interfaces/IRubyscore_Deposit.sol";

contract Rubyscore_Deposit is EIP712, AccessControl, ReentrancyGuard, IRubyscore_Deposit {
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");
    string public constant NAME = "Rubyscore_Deposit";
    string public constant VERSION = "0.0.1";


    mapping(address => uint256) private depositStorage;
    mapping(address => uint256) private userNonce;

    function getUserNonce(address userAddress) external view returns (uint256) {
        return userNonce[userAddress];
    }

    function getUserDeposit(address userAddress) external view returns (uint256) {
        return depositStorage[userAddress];
    }

    constructor(address admin, address operator) EIP712(NAME, VERSION){
        require(admin != address(0), "Zero address check");
        require(operator != address(0), "Zero address check");
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(OPERATOR_ROLE, operator);
    }

    function deposit() external payable {
        require(msg.value > 0, "Value should equal highest zero");
        uint256 amount = msg.value;
        uint256 currentDeposit = depositStorage[msg.sender];
        currentDeposit += amount;
        depositStorage[msg.sender] = currentDeposit;

        emit Deposit(msg.sender, amount);
    }

    function deposit(address recipient) external payable nonReentrant {
        require(msg.value > 0, "Value should equal highest zero");
        uint256 amount = msg.value;
        uint256 currentDeposit = depositStorage[recipient];
        currentDeposit += amount;
        depositStorage[recipient] = currentDeposit;

        emit Deposit(recipient, amount);
    }

    function withdraw(address from, address payable to, uint256 amount, uint256 tax) external onlyRole(OPERATOR_ROLE) {
        uint256 currentDeposit = depositStorage[from];
        require(currentDeposit >= amount, "Insufficient funds");
        uint256 finalAmount = amount - tax;
        (bool success,) = to.call{value: finalAmount}("");
        require(success, "Failed to send Ether");
        depositStorage[from] -= amount;

        emit Withdrawal(to, finalAmount, tax);
    }

    function withdrawBatch(address[] calldata from, address payable[] calldata to, uint256[] calldata amounts, uint256[] calldata taxes) external onlyRole(OPERATOR_ROLE) {
        for(uint256 i = 0; i < from.length; i++) {
            address sender = from[i];
            uint256 amount = amounts[i];
            uint256 tax = taxes[i];
            address payable recipient = to[i];

            require(depositStorage[sender] >= amount, "Insufficient funds");
            uint256 finalAmount = amount - tax;
            (bool success,) = recipient.call{value: finalAmount}("");
            require(success, "Failed to send Ether");
            depositStorage[sender] -= amount;

            emit Withdrawal(recipient, finalAmount, tax);
        }
    }

    function claimProfit(ClaimParams memory claimParams, bytes calldata operatorSignature) external nonReentrant {
        require(claimParams.amount > 0, "Zero amount to claim");
        require(claimParams.userNonce == userNonce[claimParams.recipient], "Nonce is invalid");

        bytes32 digest = _hashTypedDataV4(
            keccak256(
                abi.encode(
                    keccak256("ClaimParams(address recipient,uint256 amount,uint256 userNonce)"),
                    claimParams.recipient,
                    claimParams.amount,
                    claimParams.userNonce
                )
            )
        );
        _checkRole(OPERATOR_ROLE, ECDSA.recover(digest, operatorSignature));
        userNonce[claimParams.recipient] += 1;

        (bool success,) = payable(claimParams.recipient).call{value: claimParams.amount}("");
        require(success, "Failed to send Ether");

        emit Claimed(claimParams.recipient, claimParams.amount);
    }

    function addFunds() external payable onlyRole(DEFAULT_ADMIN_ROLE) {}

    function removeFunds(address payable to, uint256 amount) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(address(this).balance >= amount, "INSUFFICIENT_BALANCE");
        to.call{value: amount}("");
    }

}","keccak256":"0x6980dd670f278c1ce35bfd7fa40429264f15c144062f0c3e06586dde9ccb2c4b","license":"MIT"},"contracts/interfaces/IRubyscore_Deposit.sol":{"content":"// SPDX-License-Identifier: SEE LICENSE IN LICENSE
pragma solidity 0.8.21;

/**
 * @title IRubyscore_Deposit
 * @dev IRubyscore_Deposit is an interface for IRubyscore_Deposit contract
 */
interface IRubyscore_Deposit {
    struct ClaimParams {
        address payable recipient;
        uint256 amount;
        uint256 userNonce;
    }

    /**
    * @notice Emitted when a user makes a deposit.
    * @param user - The user who made the deposit.
    * @param amount - Amount deposited.
    */
    event Deposit(address indexed user, uint256 indexed amount);

    /**
    * @notice Emitted when a user withdraws a deposit.
    * @param user - User where the deposit was withdrawn.
    * @param amount - Withdrawal amount.
    * @param tax - Withdrawal fee.
    */
    event Withdrawal(address indexed user, uint256 indexed amount, uint256 indexed tax);

    /**
    * @notice Emitted when a user comes for profit from the referral system.
    * @param recipient - The address that receives the profit for the referral system.
    * @param amount - The address that receives the profit for the referral system.
    */
    event Claimed(address indexed recipient, uint256 indexed amount);

    /**
     * @notice Get the user's nonce associated with their address.
     * @param userAddress - The address of the user.
     * @return The user's nonce.
     */
    function getUserNonce(address userAddress) external view returns (uint256);

    /**
    * @notice Returns the user's stored deposit.
    * @param userAddress - Address to get deposit amount.
    * @return User deposit amount.
    */
    function getUserDeposit(address userAddress) external view returns (uint256);

    /**
    * @notice Accepts the user's deposit and writes it to the user's account.
    * @dev The ETH sent must be greater than zero.
    */
    function deposit() external payable;

    /**
    * @notice Accepts the user's deposit and writes it to the user's account.
    * @param recipient - The address to which the deposit is to be credited.
    * @dev The ETH sent must be greater than zero.
    */
    function deposit(address recipient) external payable;

    /**
    * @notice Withdraws the deposit to the specified address, used exclusively by the operator.
    * @dev Can only be used by the operator role.
    * @param from - Address from which to withdraw deposit.
    * @param to - Address where to send the deposit.
    * @param amount - Withdrawal amount.
    * @param tax - Withdrawal fee.
    */
    function withdraw(address from, address payable to, uint256 amount, uint256 tax) external;

    /**
    * @notice Batch withdraws the deposits to the specified addresses, used exclusively by the operator.
    * @dev Can only be used by the operator role.
    * @param from - Addresses from which to withdraw deposit.
    * @param to - Addresses where to send the deposits.
    * @param amounts - Withdrawal amounts.
    * @param taxes - Withdrawal fees.
    */
    function withdrawBatch(address[] calldata from, address payable[] calldata to, uint256[] calldata amounts, uint256[] calldata taxes) external;

    /**
    * @notice Withdraws the profit generated for the referral system. Only works if the notice is correct and the operator's signature is valid.
    * @param claimParams - Object with information about the output profit.
    * @param operatorSignature - Signature message.
    */
    function claimProfit(ClaimParams memory claimParams, bytes calldata operatorSignature) external;

    /**
    * @notice Contributes free funds to the contract.
    * @dev Can only be used by the operator role.
    */
    function addFunds() external payable;

    /**
    * @notice Withdraws funds from the contract.
    * @dev Can only be used by the operator role.
    */
    function removeFunds(address payable to, uint256 amount) external;
}","keccak256":"0xb77883a55299b5fce8c0086a7a8ef941fea1babc21611795547cb90d0f58be32","license":"SEE LICENSE IN LICENSE"}},"version":1}
